/*Table structure for table `comment` */

DROP TABLE IF EXISTS `comment`;

CREATE TABLE `comment` (
  `id` int(11) NOT NULL auto_increment COMMENT '评论id',
  `art_id` int(11) NOT NULL COMMENT '评论的文章id',
