/*Table structure for table `feedback` */

DROP TABLE IF EXISTS `feedback`;

CREATE TABLE `feedback` (
  `uf_id` int(11) NOT NULL auto_increment COMMENT '用户反馈id',
  `nickname` varchar(10) NOT NULL default '' COMMENT '账号昵称',
  `plate` varchar(10) NOT NULL COMMENT '用户选择的投诉板快',
  `uf_cont` text NOT NULL,
  `uf_email` varchar(255) NOT NULL COMMENT '用户邮箱',
  `rellyname` varchar(10) NOT NULL COMMENT '用户真实姓名',
  `option` varchar(10) NOT NULL default '未处理' COMMENT '反馈处理状态',
  PRIMARY KEY  (`uf_id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

