/*Table structure for table `notice` */

DROP TABLE IF EXISTS `notice`;

CREATE TABLE `notice` (
  `id` int(10) NOT NULL auto_increment COMMENT '公告id',
  `n_title` varchar(255) NOT NULL default '' COMMENT '公告标题',
