) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

/*Data for the table `notice` */

insert  into `notice`(`id`,`n_title`,`n_content`,`n_time`) values (1,'关于老森社区用户须知提醒','关于老森社区用户须知提醒的公告如下：欢迎加入老森社区论坛，在这里你可以畅所欲言，在本社区谨防陌生人，万不可轻信陌生人，保障自己的财产安全，一旦存在任何违法内容，本社区会保留初始证据提交警方，如果您有任何的建议或者发聩，请通过反馈入口联系我们，我们将会在一个工作日为您解答，最后，祝愿每一个加入本社区的用户在这里能够得到长久的发展。','2021-10-06 22:13:30'),(3,'关于老森社区违规账号的公告','关于老森社区违规账号的公告如下：经过用户反馈举报，后经我司查实证明，关于‘我是一只草’，‘你好坏呀’，‘白手不分离’，‘烟花易冷’，‘万能租售’，‘小钢镚’，‘M哥哥’；等多名用户进行封号处理，希望社区用户发帖规范，营造一个和谐的社区。','2021-10-06 22:18:52'),(4,'关于\'老森社区\' App即将上线的公告','关于‘老森社区’APP即将上线的公告如下：为了让广大社区用户得到更好的体验，经过管理层商议决定开发‘老森社区’APP，目前APP正在测试阶段，即将上线，届时会公告下载入口。','2021-10-06 22:21:47');

/*Table structure for table `thumbs` */

